/**
 * Check if the given character contains an alpha character, a-z, A-Z, _
 */
export declare function isAlpha(c: string): boolean;
/**
 * Check if the given character contains a hexadecimal character 0-9, a-f, A-F
 */
export declare function isHex(c: string): boolean;
/**
 * checks if the given char c is a digit
 */
export declare function isDigit(c: string): boolean;
/**
 * Check if the given character is a whitespace character like space, tab, or
 * newline
 */
export declare function isWhitespace(c: string): boolean;
export declare function isSpecialWhitespace(c: string): boolean;
export declare function normalizeWhitespace(text: string): string;
export declare function isQuote(c: string): boolean;
export declare function normalizeQuote(c: string): string;
export declare function stripLastOccurrence(text: string, textToStrip: string): string;
export declare function insertBeforeLastWhitespace(text: string, textToInsert: string): string;
export declare function insertAtIndex(text: string, textToInsert: string, index: number): string;
